mapper.select:
WITH TEMP_PRODUCTION_PLAN AS ( select b.code as code, b.name as name, b.project_name as projectName, (select sum(c.plan_value) from production_plan c where c.code = b.code and c.project_name = b.project_name
AND c.plan_date >= #{dateStart}
AND c.plan_date <= #{dateEnd}
) as sumQty, ${selectColumn} from ( select code, name, project_name, plan_value, plan_date from production_plan where 1=1 AND code not in ('JHXNLIANGLV', 'JHHDLIANGLV', 'MBLIANGLV', 'JHZHITONGLV')
AND project_name = #{projectName}
AND plan_date >= #{dateStart}
AND plan_date <= #{dateEnd}
) as a pivot(sum(plan_value) for plan_date in (${pivotIn})) as b group by b.project_name, b.code, b.name), TEMP_JHTOURU AS ( SELECT projectName, code, name, sumQty, ${pivotIn} FROM TEMP_PRODUCTION_PLAN WHERE code = 'JHTOURU'), TEMP_MBLIANGLV AS ( SELECT projectName, code, name, sumQty, ${pivotIn} FROM TEMP_PRODUCTION_PLAN WHERE code = 'MBLIANGLV'), TEMP_JHXUESHU AS ( SELECT projectName, code, name, sumQty, ${pivotIn} FROM TEMP_PRODUCTION_PLAN WHERE code = 'JHXUESHU'), TEMP_JHXNLIANGLV AS ( SELECT projectName, code, name, sumQty, ${pivotIn} FROM TEMP_PRODUCTION_PLAN WHERE code = 'JHXNLIANGLV'), TEMP_JHHDLIANGLV AS ( SELECT projectName, code, name, sumQty, ${pivotIn} FROM TEMP_PRODUCTION_PLAN WHERE code = 'JHHDLIANGLV'), TEMP_JHCHANCHU AS ( SELECT TEMP_JHTOURU.projectName projectName, 'JHCHANCHU' code, '计划模压产出片数(PCS)' name, TEMP_JHTOURU.sumQty * TEMP_MBLIANGLV.sumQty sumQty, ${selectJHCHANCHUColumn} FROM TEMP_JHTOURU LEFT JOIN TEMP_MBLIANGLV ON TEMP_JHTOURU.projectName = TEMP_MBLIANGLV.projectName ), TEMP_JHLINGLIAO AS ( SELECT TEMP_JHCHANCHU.projectName projectName, 'JHLINGLIAO' code, '计划后道领料(PCS)' name, TEMP_JHCHANCHU.sumQty * TEMP_JHXNLIANGLV.sumQty sumQty, ${selectJHLINGLIAOColumn} FROM TEMP_JHCHANCHU LEFT JOIN TEMP_JHXNLIANGLV ON TEMP_JHCHANCHU.projectName = TEMP_JHXNLIANGLV.projectName ), TEMP_JHHDCHANCHU AS ( SELECT TEMP_JHXUESHU.projectName projectName, 'JHHDCHANCHU' code, '计划后道产出（颗)' name, TEMP_JHXUESHU.sumQty * TEMP_JHLINGLIAO.sumQty * TEMP_JHHDLIANGLV.sumQty sumQty, ${selectJHHDCHANCHUColumn} FROM TEMP_JHXUESHU LEFT JOIN TEMP_JHLINGLIAO ON TEMP_JHXUESHU.projectName = TEMP_JHLINGLIAO.projectName LEFT JOIN TEMP_JHHDLIANGLV ON TEMP_JHXUESHU.projectName = TEMP_JHHDLIANGLV.projectName ), TEMP_PRODUCTION_ACTUAL_VALUE AS ( SELECT project_name, actual_date, code, sum(actual_value) actual_value FROM production_actual UNPIVOT (actual_value FOR code IN ( estimate_hole_qty, mold_press_input_qty, mold_press_output_qty, after_acquisition_qty, after_input_qty, after_output_qty, inventory_qty )) as b where 1=1 AND code not in ('JHXNLIANGLV', 'JHHDLIANGLV', 'MBLIANGLV', 'JHZHITONGLV')
AND project_name = #{projectName}
AND actual_date >= #{dateStart}
AND actual_date <= #{dateEnd}
group by project_name, actual_date, code ), TEMP_PRODUCTION_ACTUAL AS ( select d.project_name projectName, CASE WHEN d.code = 'after_acquisition_qty' THEN 'SJLINGLIAO' WHEN d.code = 'after_input_qty' THEN 'SJHDTOULIAO' WHEN d.code = 'after_output_qty' THEN 'SJHDCHANCHU' WHEN d.code = 'estimate_hole_qty' THEN 'SJXUESHU' WHEN d.code = 'mold_press_input_qty' THEN 'SJTOURU' WHEN d.code = 'mold_press_output_qty' THEN 'SJCHANCHU' WHEN d.code = 'performance_yield' THEN 'SJXNLIANGLV' WHEN d.code = 'after_yield' THEN 'SJHDLIANGLV' WHEN d.code = 'inventory_qty' THEN 'SJRUKU' ELSE d.code END as code, CASE WHEN d.code = 'after_acquisition_qty' THEN '实际后道领料(PCS)' WHEN d.code = 'after_input_qty' THEN '后道实际投料(PCS)' WHEN d.code = 'after_output_qty' THEN '实际后道产出（颗)' WHEN d.code = 'estimate_hole_qty' THEN '实际预估收穴数' WHEN d.code = 'mold_press_input_qty' THEN '实际模压投入片数（PCS)' WHEN d.code = 'mold_press_output_qty' THEN '实际模压产出片数(PCS)' WHEN d.code = 'inventory_qty' THEN '实际入库（转镀膜）' ELSE d.code END as name, ( select sum(TEMP_PRODUCTION_ACTUAL_VALUE.actual_value) from TEMP_PRODUCTION_ACTUAL_VALUE where TEMP_PRODUCTION_ACTUAL_VALUE.code = d.code and TEMP_PRODUCTION_ACTUAL_VALUE.project_name = d.project_name
AND TEMP_PRODUCTION_ACTUAL_VALUE.actual_date >= #{dateStart}
AND TEMP_PRODUCTION_ACTUAL_VALUE.actual_date <= #{dateEnd}
) sumQty, ${selectColumn} from (SELECT * FROM TEMP_PRODUCTION_ACTUAL_VALUE ) as c pivot (max(actual_value) for actual_date in (${pivotIn})) as d group by d.project_name, d.code ), TEMP_PRODUCTION_REPORT AS ( SELECT projectName, code, name, cast(floor(ROUND(sumQty,0)) as varchar (50)) sumQty, ${selectVarcharColumn} from TEMP_PRODUCTION_PLAN UNION ALL SELECT '汇总' projectName, code, name, cast(floor(ROUND(sum(sumQty), 0)) as varchar(50)) sumQty, ${selectSumVarcharColumn} from TEMP_PRODUCTION_PLAN group by code, name UNION ALL SELECT projectName, code, name, cast(floor(ROUND(sumQty,0)) as varchar (50)) sumQty, ${selectVarcharColumn} from TEMP_JHHDCHANCHU UNION ALL SELECT '汇总' projectName, code, name, cast(floor(ROUND(sum(sumQty), 0)) as varchar(50)) sumQty, ${selectSumVarcharColumn} from TEMP_JHHDCHANCHU group by code, name UNION ALL SELECT projectName, code, name, cast(floor(ROUND(sumQty, 0)) as varchar (50)) sumQty, ${selectVarcharColumn} from TEMP_PRODUCTION_ACTUAL UNION ALL SELECT '汇总' projectName, code, name, cast(floor(ROUND(sum(sumQty), 0)) as varchar(50)) sumQty, ${selectSumVarcharColumn} from TEMP_PRODUCTION_ACTUAL group by code, name UNION ALL SELECT TP.projectName, 'RUKUDCL' code, '实际产出达成率' name, cast(CAST(ISNULL(TA.sumQty, 0) / TP.sumQty * 100 as decimal(18, 2) )as varchar(50)) +'%' sumQty, ${selectRateColumn} from (select * from TEMP_PRODUCTION_PLAN where code = 'JHHDCHANCHU') TP LEFT JOIN (select * from TEMP_PRODUCTION_ACTUAL TA where code = 'SJHDCHANCHU') TA ON TA.projectName = TP.projectName UNION ALL SELECT '汇总' projectName, 'RUKUDCL' code, '实际产出达成率' name, cast(CAST(sum(TA.sumQty) / sum(TP.sumQty) * 100 as decimal(18, 2) )as varchar(50)) +'%' sumQty, ${selectSumRateColumn} from (select * from TEMP_PRODUCTION_PLAN where code = 'JHHDCHANCHU') TP LEFT JOIN (select * from TEMP_PRODUCTION_ACTUAL TA where code = 'SJHDCHANCHU') TA ON 1=1 ) SELECT ROW_NUMBER() OVER ( ORDER BY projectName, code ) AS seq, * FROM TEMP_PRODUCTION_REPORT ORDER BY projectName, code

select distinct CONVERT(varchar(100), plan_date, 23) produciton_date
from production_plan
where 1=1

    AND project_name = #{projectName}


    AND mold = #{mold}


    AND cycle = #{cycle}


    AND plan_date >= #{dateStart}


    AND plan_date <= #{dateEnd}

union
select distinct CONVERT(varchar(100), actual_date, 23) produciton_date
from production_actual
where 1=1

    AND project_name = #{projectName}


    AND mold = #{mold}


    AND cycle = #{cycle}


    AND actual_date >= #{dateStart}


    AND actual_date <= #{dateEnd}

order by produciton_date

WITH TEMP_PRODUCTION_PLAN AS ( SELECT plan_date, project_name, mold, cycle, code, plan_value FROM production_plan where 1 = 1
AND project_name = #{projectName}
AND mold = #{mold}
AND cycle = #{cycle}
AND plan_date >= #{dateStart}
AND plan_date <= #{dateEnd}
) SELECT ROW_NUMBER() OVER ( ORDER BY productionDate, projectName, mold, cycle ) AS seq, productionDate, projectName, mold, cycle, JHXUESHU, JHHDCHANCHU, JHCHANCHU, estimateHoleQty, afterOutputQty, fpy, inventoryQty, YUGUMOYA from ( select case when c.actual_date is not null then c.actual_date else b.plan_date end productionDate, case when c.project_name is not null then c.project_name else b.project_name end projectName, case when c.mold is not null then c.mold else b.mold end mold, case when c.cycle is not null then c.cycle else b.cycle end cycle, b.JHXUESHU, b.JHCHANCHU, TF.JHHDCHANCHU, c.estimate_hole_qty estimateHoleQty, c.after_output_qty afterOutputQty, c.inventory_qty inventoryQty, cast(cast(d.fpy * 100 as decimal(18, 2)) as varchar(50)) + '%' fpy, floor(ROUND(c.mold_press_input_qty * c.estimate_hole_qty * d.fpy, 0)) YUGUMOYA FROM TEMP_PRODUCTION_PLAN AS a PIVOT (max(plan_value) FOR code IN ([JHXUESHU], [JHCHANCHU])) AS b FULL JOIN ( select actual_date, project_name, mold, cycle, sum(ISNULL(estimate_hole_qty, 0)) estimate_hole_qty, sum(ISNULL(after_output_qty, 0)) after_output_qty, sum(ISNULL(inventory_qty, 0)) inventory_qty, sum(ISNULL(mold_press_input_qty, 0)) mold_press_input_qty from production_actual where 1=1
AND project_name = #{projectName}
AND mold = #{mold}
AND cycle = #{cycle}
AND actual_date >= #{dateStart}
AND actual_date <= #{dateEnd}
group by actual_date, project_name, mold, cycle) c ON c.project_name = b.project_name and c.mold = b.mold and c.cycle = b.cycle and c.actual_date = b.plan_date LEFT join estimate_fpy d ON c.project_name = d.project_name AND c.mold = d.mold AND c.cycle = d.cycle AND c.actual_date = d.fpy_date LEFT JOIN (SELECT TA.project_name, TA.mold, TA.cycle, 'JHHDCHANCHU' code, '计划后道产出（颗)' name, TA.plan_date, FLOOR(ROUND(TA.plan_value * TB.plan_value * TC.plan_value * TD.plan_value * TE.plan_value, 0)) JHHDCHANCHU FROM (SELECT * FROM TEMP_PRODUCTION_PLAN WHERE code = 'JHXUESHU') TA LEFT JOIN (SELECT * FROM TEMP_PRODUCTION_PLAN WHERE code = 'JHTOURU') TB ON TA.project_name = TB.project_name AND TA.mold = TB.mold AND TA.cycle = TB.cycle AND TA.plan_date = TB.plan_date LEFT JOIN (SELECT * FROM TEMP_PRODUCTION_PLAN WHERE code = 'MBLIANGLV') TC ON TA.project_name = TC.project_name AND TA.mold = TC.mold AND TA.cycle = TC.cycle AND TA.plan_date = TC.plan_date LEFT JOIN (SELECT * FROM TEMP_PRODUCTION_PLAN WHERE code = 'JHXNLIANGLV') TD ON TA.project_name = TD.project_name AND TA.mold = TD.mold AND TA.cycle = TD.cycle AND TA.plan_date = TD.plan_date LEFT JOIN (SELECT * FROM TEMP_PRODUCTION_PLAN WHERE code = 'JHHDLIANGLV') TE ON TA.project_name = TE.project_name AND TA.mold = TE.mold AND TA.cycle = TE.cycle AND TA.plan_date = TE.plan_date ) TF ON b.project_name = TF.project_name AND b.mold = TF.mold AND b.cycle = TF.cycle AND b.plan_date = TF.plan_date ) as t order by productionDate, projectName, mold, cycle

WITH TEMP_PRODUCTION_PLAN AS ( select b.code as code, b.name as name, b.project_name as projectName, b.mold as mold, b.cycle as cycle, (select case when b.code in ('JHXNLIANGLV', 'MBLIANGLV', 'JHHDLIANGLV', 'JHZHITONGLV') then sum(c.plan_value) / count(1) else sum(c.plan_value) end from production_plan c where c.code = b.code and c.project_name = b.project_name and c.mold = b.mold and c.cycle = b.cycle
AND c.plan_date >= #{dateStart}
AND c.plan_date <= #{dateEnd}
) as sumQty, (select max(c.plan_value) from production_plan c where c.code = b.code and c.project_name = b.project_name and c.mold = b.mold and c.cycle = b.cycle
AND c.plan_date >= #{dateStart}
AND c.plan_date <= #{dateEnd}
) as maxQty, ${selectColumn} from ( select code, name, project_name, mold, cycle, plan_value, plan_date from production_plan where 1=1
AND project_name = #{projectName}
AND mold = #{mold}
AND cycle = #{cycle}
AND plan_date >= #{dateStart}
AND plan_date <= #{dateEnd}
) as a pivot(sum(plan_value) for plan_date in (${pivotIn})) as b group by b.project_name, b.code, b.name, b.mold, b.cycle), TEMP_JHTOURU AS ( SELECT projectName, mold, cycle, code, name, sumQty, maxQty, ${pivotIn} FROM TEMP_PRODUCTION_PLAN WHERE code = 'JHTOURU'), TEMP_MBLIANGLV AS ( SELECT projectName, mold, cycle, code, name, sumQty, maxQty, ${pivotIn} FROM TEMP_PRODUCTION_PLAN WHERE code = 'MBLIANGLV'), TEMP_JHXUESHU AS ( SELECT projectName, mold, cycle, code, name, sumQty, maxQty, ${pivotIn} FROM TEMP_PRODUCTION_PLAN WHERE code = 'JHXUESHU'), TEMP_JHXNLIANGLV AS ( SELECT projectName, mold, cycle, code, name, sumQty, maxQty, ${pivotIn} FROM TEMP_PRODUCTION_PLAN WHERE code = 'JHXNLIANGLV'), TEMP_JHHDLIANGLV AS ( SELECT projectName, mold, cycle, code, name, sumQty, maxQty, ${pivotIn} FROM TEMP_PRODUCTION_PLAN WHERE code = 'JHHDLIANGLV'), TEMP_JHCHANCHU AS ( SELECT TEMP_JHTOURU.projectName projectName, TEMP_JHTOURU.mold mold, TEMP_JHTOURU.cycle cycle, 'JHCHANCHU' code, '计划模压产出片数(PCS)' name, ${selectJHCHANCHUSumColumn} sumQty, TEMP_JHTOURU.maxQty * TEMP_MBLIANGLV.maxQty maxQty, ${selectJHCHANCHUColumn} FROM TEMP_JHTOURU LEFT JOIN TEMP_MBLIANGLV ON TEMP_JHTOURU.projectName = TEMP_MBLIANGLV.projectName AND TEMP_JHTOURU.mold = TEMP_MBLIANGLV.mold AND TEMP_JHTOURU.cycle = TEMP_MBLIANGLV.cycle ), TEMP_JHLINGLIAO AS ( SELECT TEMP_JHCHANCHU.projectName projectName, TEMP_JHCHANCHU.mold mold, TEMP_JHCHANCHU.cycle cycle, 'JHLINGLIAO' code, '计划后道领料(PCS)' name, ${selectJHLINGLIAOSumColumn} sumQty, TEMP_JHCHANCHU.maxQty * TEMP_JHXNLIANGLV.maxQty maxQty, ${selectJHLINGLIAOColumn} FROM TEMP_JHCHANCHU LEFT JOIN TEMP_JHXNLIANGLV ON TEMP_JHCHANCHU.projectName = TEMP_JHXNLIANGLV.projectName AND TEMP_JHCHANCHU.mold = TEMP_JHXNLIANGLV.mold AND TEMP_JHCHANCHU.cycle = TEMP_JHXNLIANGLV.cycle ), TEMP_JHHDCHANCHU AS ( SELECT TEMP_JHXUESHU.projectName projectName, TEMP_JHXUESHU.mold mold, TEMP_JHXUESHU.cycle cycle, 'JHHDCHANCHU' code, '计划后道产出（颗)' name, ${selectJHHDCHANCHUSumColumn} sumQty, TEMP_JHXUESHU.maxQty * TEMP_JHLINGLIAO.maxQty * TEMP_JHHDLIANGLV.maxQty maxQty, ${selectJHHDCHANCHUColumn} FROM TEMP_JHXUESHU LEFT JOIN TEMP_JHLINGLIAO ON TEMP_JHXUESHU.projectName = TEMP_JHLINGLIAO.projectName AND TEMP_JHXUESHU.mold = TEMP_JHLINGLIAO.mold AND TEMP_JHXUESHU.cycle = TEMP_JHLINGLIAO.cycle LEFT JOIN TEMP_JHHDLIANGLV ON TEMP_JHXUESHU.projectName = TEMP_JHHDLIANGLV.projectName AND TEMP_JHXUESHU.mold = TEMP_JHHDLIANGLV.mold AND TEMP_JHXUESHU.cycle = TEMP_JHHDLIANGLV.cycle ), TEMP_JHZHITONGLV AS ( SELECT TEMP_JHXNLIANGLV.projectName projectName, TEMP_JHXNLIANGLV.mold mold, TEMP_JHXNLIANGLV.cycle cycle, 'JHZHITONGLV' code, '计划后道直通率' name, TEMP_JHXNLIANGLV.sumQty * TEMP_JHHDLIANGLV.sumQty sumQty, TEMP_JHXNLIANGLV.maxQty * TEMP_JHHDLIANGLV.maxQty maxQty, ${selectJHZHITONGLVColumn} FROM TEMP_JHXNLIANGLV LEFT JOIN TEMP_JHHDLIANGLV ON TEMP_JHXNLIANGLV.projectName = TEMP_JHHDLIANGLV.projectName AND TEMP_JHXNLIANGLV.mold = TEMP_JHHDLIANGLV.mold AND TEMP_JHXNLIANGLV.cycle = TEMP_JHHDLIANGLV.cycle ), TEMP_PRODUCTION_PLAN_RESULT AS ( SELECT projectName, mold, cycle, code, name, sumQty, maxQty, ${selectDateColumn} FROM TEMP_JHCHANCHU UNION ALL SELECT projectName, mold, cycle, code, name, sumQty, maxQty, ${selectDateColumn} FROM TEMP_JHLINGLIAO UNION ALL SELECT projectName, mold, cycle, 'JHHDTOULIAO' code, '后道计划投料(PCS)' name, sumQty, maxQty, ${selectDateColumn} FROM TEMP_JHLINGLIAO UNION ALL SELECT projectName, mold, cycle, code, name, sumQty, maxQty, ${selectDateColumn} FROM TEMP_JHHDCHANCHU UNION ALL SELECT projectName, mold, cycle, code, name, sumQty, maxQty, ${selectDateColumn} FROM TEMP_JHZHITONGLV ), TEMP_PRODUCTION_ACTUAL_VALUE AS ( SELECT project_name, actual_date, code, mold, cycle, sum(actual_value) actual_value FROM production_actual UNPIVOT (actual_value FOR code IN ( performance_yield, after_yield )) as a where 1=1
AND project_name = #{projectName}
AND mold = #{mold}
AND cycle = #{cycle}
AND actual_date >= #{dateStart}
AND actual_date <= #{dateEnd}
group by project_name, actual_date, code, mold, cycle union all SELECT project_name, actual_date, code, mold, cycle, sum(actual_value) actual_value FROM production_actual UNPIVOT (actual_value FOR code IN ( estimate_hole_qty, mold_press_input_qty, mold_press_output_qty, after_acquisition_qty, after_input_qty, after_output_qty, inventory_qty )) as b where 1=1
AND project_name = #{projectName}
AND mold = #{mold}
AND cycle = #{cycle}
AND actual_date >= #{dateStart}
AND actual_date <= #{dateEnd}
group by project_name, actual_date, code, mold, cycle ), TEMP_PRODUCTION_ACTUAL AS ( select d.project_name projectName, d.mold, d.cycle, CASE WHEN d.code = 'after_acquisition_qty' THEN 'SJLINGLIAO' WHEN d.code = 'after_input_qty' THEN 'SJHDTOULIAO' WHEN d.code = 'after_output_qty' THEN 'SJHDCHANCHU' WHEN d.code = 'estimate_hole_qty' THEN 'SJXUESHU' WHEN d.code = 'mold_press_input_qty' THEN 'SJTOURU' WHEN d.code = 'mold_press_output_qty' THEN 'SJCHANCHU' WHEN d.code = 'performance_yield' THEN 'SJXNLIANGLV' WHEN d.code = 'after_yield' THEN 'SJHDLIANGLV' WHEN d.code = 'inventory_qty' THEN 'SJRUKU' ELSE d.code END as code, CASE WHEN d.code = 'after_acquisition_qty' THEN '实际后道领料(PCS)' WHEN d.code = 'after_input_qty' THEN '后道实际投料(PCS)' WHEN d.code = 'after_output_qty' THEN '实际后道产出（颗)' WHEN d.code = 'estimate_hole_qty' THEN '实际预估收穴数' WHEN d.code = 'mold_press_input_qty' THEN '实际模压投入片数（PCS)' WHEN d.code = 'mold_press_output_qty' THEN '实际模压产出片数(PCS)' WHEN d.code = 'performance_yield' THEN '实际性能良率' WHEN d.code = 'after_yield' THEN '实际后道良率' WHEN d.code = 'inventory_qty' THEN '实际入库（转镀膜）' ELSE d.code END as name, (select case when d.code in ('performance_yield', 'after_yield') then sum(TEMP_PRODUCTION_ACTUAL_VALUE.actual_value) / count(1) else sum(TEMP_PRODUCTION_ACTUAL_VALUE.actual_value) end from TEMP_PRODUCTION_ACTUAL_VALUE where TEMP_PRODUCTION_ACTUAL_VALUE.code = d.code and TEMP_PRODUCTION_ACTUAL_VALUE.project_name = d.project_name and TEMP_PRODUCTION_ACTUAL_VALUE.mold = d.mold and TEMP_PRODUCTION_ACTUAL_VALUE.cycle = d.cycle
AND TEMP_PRODUCTION_ACTUAL_VALUE.actual_date >= #{dateStart}
AND TEMP_PRODUCTION_ACTUAL_VALUE.actual_date <= #{dateEnd}
) sumQty, (select max(TEMP_PRODUCTION_ACTUAL_VALUE.actual_value) from TEMP_PRODUCTION_ACTUAL_VALUE where TEMP_PRODUCTION_ACTUAL_VALUE.code = d.code and TEMP_PRODUCTION_ACTUAL_VALUE.project_name = d.project_name and TEMP_PRODUCTION_ACTUAL_VALUE.mold = d.mold and TEMP_PRODUCTION_ACTUAL_VALUE.cycle = d.cycle
AND TEMP_PRODUCTION_ACTUAL_VALUE.actual_date >= #{dateStart}
