) maxQty, ${selectColumn} from (SELECT * FROM TEMP_PRODUCTION_ACTUAL_VALUE ) as c pivot (max(actual_value) for actual_date in (${pivotIn})) as d group by d.project_name, d.code, d.mold, d.cycle ), TEMP_SJLIANGLV AS( SELECT TPA.projectName, TPA.mold, TPA.cycle, 'SJLIANGLV' code, '实际模压成型良率' name, ISNULL(TCC.sumQty/TPA.sumQty, 0) sumQty, ISNULL(TCC.maxQty/TPA.maxQty, 0) maxQty, ${selectSJLIANGLVColumn} FROM (SELECT * FROM TEMP_PRODUCTION_ACTUAL WHERE code = 'SJTOURU') TPA LEFT JOIN (SELECT * FROM TEMP_PRODUCTION_ACTUAL WHERE code = 'SJCHANCHU') TCC ON TPA.projectName=TCC.projectName AND TPA.mold = TCC.mold AND TPA.cycle = TCC.cycle ), TEMP_SJZHITONGLV AS( SELECT TPA.projectName, TPA.mold, TPA.cycle, 'SJZHITONGLV' code, '实际后道直通率' name, TPA.sumQty*TPB.sumQty sumQty, TPA.maxQty*TPB.maxQty maxQty, ${selectSJZHITONGLVColumn} FROM (SELECT * FROM TEMP_PRODUCTION_ACTUAL WHERE code = 'SJXNLIANGLV') TPA LEFT JOIN (SELECT * FROM TEMP_PRODUCTION_ACTUAL WHERE code= 'SJHDLIANGLV') TPB ON TPA.projectName=TPB.projectName AND TPA.mold = TPB.mold AND TPA.cycle = TPB.cycle ), TEMP_CHANCHUDCL AS ( SELECT TPA.projectName, TPA.mold, TPA.cycle, 'CHANCHUDCL' code, '模次产出达成率' name, TPA.sumQty/TPB.sumQty sumQty, TPA.maxQty/TPB.maxQty maxQty, ${selectDCLVColumn} FROM (SELECT * FROM TEMP_PRODUCTION_ACTUAL WHERE code = 'SJCHANCHU') TPA LEFT JOIN (SELECT * FROM TEMP_JHCHANCHU) TPB ON TPA.projectName=TPB.projectName AND TPA.mold = TPB.mold AND TPA.cycle = TPB.cycle ), TEMP_RUKUDCL AS ( SELECT TPA.projectName, TPA.mold, TPA.cycle, 'RUKUDCL' code, '入库达成率' name, TPA.sumQty/TPB.sumQty sumQty, TPA.maxQty/TPB.maxQty maxQty, ${selectDCLVColumn} FROM (SELECT * FROM TEMP_PRODUCTION_ACTUAL WHERE code = 'SJHDCHANCHU') TPA LEFT JOIN (SELECT * FROM TEMP_JHHDCHANCHU) TPB ON TPA.projectName=TPB.projectName AND TPA.mold = TPB.mold AND TPA.cycle = TPB.cycle ), TEMP_SJLV AS ( SELECT * FROM TEMP_SJLIANGLV UNION ALL SELECT * FROM TEMP_SJZHITONGLV ), TEMP_DCL AS ( SELECT * FROM TEMP_CHANCHUDCL UNION ALL SELECT * FROM TEM